mapper.select:
SELECT COUNT(*) as cnt FROM creates
WHERE cfoodno=#{cfoodno}
WHERE cfoodno=#{cfoodno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT createsno, manageno, cfoodno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, r FROM ( SELECT createsno, manageno, cfoodno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, rownum as r FROM ( SELECT createsno, manageno, cfoodno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube FROM creates
WHERE cfoodno=#{cfoodno}
WHERE cfoodno=#{cfoodno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY createsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM creates
WHERE createsno = #{createsno} AND passwd=#{passwd}

mapper.update:
UPDATE creates
SET title=#{title}, content=#{content},  word=#{word}
WHERE createsno = #{createsno}

UPDATE creates
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE createsno = #{createsno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM creates 
WHERE cfoodno=#{cfoodno}

mapper.update:
UPDATE creates
SET replycnt = replycnt + 1
WHERE createsno = #{createsno}

UPDATE creates
SET replycnt = replycnt - 1
WHERE createsno = #{createsno}
